css:
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+Ethiopic:wght@400;700&display=swap');

body {
    font-family: 'Noto Sans Ethiopic', sans-serif;
    text-align: center;
    margin: 0;
    padding: 0;
    background: #f8f8f8;
}

.container {
    width: 350px;
    margin: 20px auto;
    background: #fff;
    padding: 20px;
    border: 1px solid #000;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
}

.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px dashed #000;
    padding-bottom: 10px;
}

.logo {
    width: 50px;
    height: 50px;
    background: #ccc;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
}

h1, h2, h3 {
    margin: 5px 0;
}

.bold {
    font-weight: bold;
}

.highlight {
    background: lightblue;
    padding: 2px 5px;
}

.divider {
    border-top: 2px dashed black;
    margin: 10px 0;
}

.big-text {
    font-size: 24px;
    font-weight: bold;
}

@media print {
    body {
        background: none;
    }

    .container {
        width: 100%;
        border: none;
        box-shadow: none;
    }

    .logo {
        background: none;
    }
}
